import { Component, OnInit } from '@angular/core';
import { Chart,registerables } from 'node_modules/chart.js';
Chart.register(...registerables)

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit{
  title = 'frontcab';
  ngOnInit(): void{
    this.RenderChart();
    this.otherChart();

  }
  RenderChart(){

    const myChart = new Chart("myChart",{
      type:'bar',
      data:{
        labels:['red','blue','yellow','green','purple','orange'],
        datasets: [{
          label: '# of votes',
          data:[12,19,3,5,2,3],
          backgroundColor:[
            'rgb(255,69,0) ',
            'rgb(255,69,0) ',
            'rgb(255,69,0) ',
            'rgb(255,69,0) ',
            'rgb(255,69,0) ',
            'rgb(255,69,0) ',
          ],
          borderColor:[
            'rgb(255,69,0) ',
            'rgb(255,69,0) ',
            'rgb(255,69,0) ',
            'rgb(255,69,0) ',
            'rgb(255,69,0) ',
            'rgb(255,69,0) ',
          ],
          borderWidth:1
        }]
      },
      options:{
        scales:{
          y:{
            beginAtZero: true
          }
        }
      }
    });
  }
  otherChart(){

    const myChart = new Chart("otherChart",{
      type:'line',
      data:{
        labels:['red','blue','yellow','green','purple','orange'],
        datasets: [{
          label: '# of votes',
          data:[12,19,3,5,2,3],
          backgroundColor:[
            '#0000FF',
            '#0000FF ',
            '#0000FF ',
            '#0000FF',
            '#0000FF',
            '#0000FF',
          ],
          borderColor:[
            '#0000FF',
            '#0000FF ',
            '#0000FF ',
            '#0000FF',
            '#0000FF',
            '#0000FF',
          ],
          borderWidth:1
        }]
      },
      options:{
        scales:{
          y:{
            beginAtZero: true
          }
        }
      }
    });
  }
}
